              val harFormue = hvorMinstEnForekomst {
                    (fastsatt(formuesverdiforVirtuellEiendel.beloep) erStørreEnn 0) 
              }
               val harIkkeTap = hvorMinstEnForekomst {
                    (fastsatt(skattepliktigTapVedRealisasjonAvVirtuellEiendel.fradragsberettigetBeloep.beloep) erLik 0)
               }
               val harIkkeGevinst = hvorMinstEnForekomst {
                    (fastsatt(skattepliktigGevinstVedRealisasjonAvVirtuellEiendel.beloep) erLik 0)
               } 
                hvis(harFormue og harIkkeTap og harIkkeGevinst)
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadInformasjon)
                }
            }
        }

sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

       val harUtslagPaaKontroll = utslagPåKontroll("UV_VIRTUELL_EIENDEL_KRYPTOVALUTA")

       val harVirtuellEiendelIFjor = fjoraaretsSkattemelding
             .forForekomsterAv { virtuellEiendel }
             .hvorMinstEnForekomst {
                 (fastsatt(formuesverdiforVirtuellEiendel.beloep) erStørreEnn 0) eller
                 (fastsatt(skattepliktigGevinstVedRealisasjonAvVirtuellEiendel.beloep) erStørreEnn 0) eller
                 (fastsatt(skattepliktigTapVedRealisasjonAvVirtuellEiendel.fradragsberettigetBeloep.beloep) erStørreEnn 0)}

        val partsnr = skattepliktGjeldende
            .forForekomsterAv { skatteplikt }
            .verdiFor { partsnummer }      

        // Kontrollen slår ut når skattepliktige har virtuell valuta for fjoråret (formue, gevinst eller tap)
        // men ikke har oppgitt virtuell valuta for inntektsåret
        // skal ikke gi utslag hvis det er utslag kontroll for Utvidet veiledning
        // AB-test: skattytere som har tallet 1, 2, 3, 4 og 9 i 7.siffer i partsnummeret får utslag
        skattemelding
            .utslagFor {
                val harVirtuellEiendelIAar = hvorMinstEnForekomst{virtuellEiendel er oppgitt}

                hvis(ikke(harUtslagPaaKontroll) og
                     harVirtuellEiendelIFjor og ikke(harVirtuellEiendelIAar) og
                     (partsnr.tegnFraIndeks(7).finnesBlant("1","2","3","4","9")))
                {
                    betjeningsstrategiSettesTil(
                        ikke, merknadTips)
                }
            }
        }

