sakstype(Personlig)
    .kontekst(ValideringSkattepliktig) {

        // kontrollen gir utslag dersom skattepliktige egenfastsetter stort fradrag i underkort for virtuell valuta/ kryptovaluta
        // skal kun gi oppgave i SME ved verdi > 100 000

       skattemelding
            .forForekomsterAv{virtuellEiendel}
            .utslagForFørste {
                val harTap = hvorMinstEnForekomst {
                    (grunnlag(skattepliktigTapVedRealisasjonAvVirtuellEiendel.fradragsberettigetBeloep.beloep) erLik 0) og
                    ((fastsatt(skattepliktigTapVedRealisasjonAvVirtuellEiendel.fradragsberettigetBeloep.beloep) - 
                      grunnlag(skattepliktigTapVedRealisasjonAvVirtuellEiendel.fradragsberettigetBeloep.beloep)) erStørreEnn 100_000)
                }

                val harForvaltningskostnader = hvorMinstEnForekomst {
                    (grunnlag(forvaltningskostnadForVirtuellEiendel.fradragsberettigetBeloep.beloep) erLik 0) og
                    ((fastsatt(forvaltningskostnadForVirtuellEiendel.fradragsberettigetBeloep.beloep) - 
                      grunnlag(forvaltningskostnadForVirtuellEiendel.fradragsberettigetBeloep.beloep)) erStørreEnn 100_000)
                }

                val harMining = hvorMinstEnForekomst {
                    (grunnlag(tapVedUtvinning.fradragsberettigetBeloep.beloep) erLik 0) og
                    ((fastsatt(tapVedUtvinning.fradragsberettigetBeloep.beloep) - 
                      grunnlag(tapVedUtvinning.fradragsberettigetBeloep.beloep)) erStørreEnn 100_000)
                }

                hvis(harTap eller harForvaltningskostnader eller harMining) {
                    betjeningsstrategiSettesTil(
                        ikke, merknadStandard
                    )
                }
            }
        }

